import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  Alert,
  CircularProgress,
  LinearProgress,
  Chip,
  Paper,
} from "@mui/material";
import {
  PlayArrow as PlayIcon,
  Refresh as RefreshIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  Pending as PendingIcon,
} from "@mui/icons-material";
import { useParams, useNavigate } from "react-router-dom";
import { crawlingAPI, keywordAPI } from "../services/api";

const CrawlingPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const projectId = parseInt(id || "0");

  const [urls, setUrls] = useState<string>("");
  const [keywords, setKeywords] = useState<any[]>([]);
  const [crawlingStatus, setCrawlingStatus] = useState<any>(null);
  const [crawlingResults, setCrawlingResults] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [crawling, setCrawling] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (projectId) {
      fetchKeywords();
      fetchCrawlingStatus();
    }
  }, [projectId]);

  const fetchKeywords = async () => {
    try {
      const response = await keywordAPI.getKeywords(projectId);
      setKeywords(response.data.keywords);
    } catch (error) {
      console.error("키워드 조회 오류:", error);
    }
  };

  const fetchCrawlingStatus = async () => {
    try {
      const response = await crawlingAPI.getCrawlingStatus(projectId);
      setCrawlingStatus(response.data);

      // 크롤링 결과도 함께 조회
      const resultsResponse = await crawlingAPI.getCrawlingResults(projectId);
      setCrawlingResults(resultsResponse.data.results || []);
    } catch (error) {
      console.error("크롤링 상태 조회 오류:", error);
    }
  };

  const handleStartCrawling = async () => {
    if (!urls.trim()) {
      setError("URL을 입력해주세요.");
      return;
    }

    if (keywords.length === 0) {
      setError("먼저 키워드를 추가해주세요.");
      return;
    }

    const urlList = urls
      .split("\n")
      .map((url) => url.trim())
      .filter((url) => url.length > 0);

    if (urlList.length === 0) {
      setError("유효한 URL이 없습니다.");
      return;
    }

    try {
      setCrawling(true);
      setError(null);

      const response = await crawlingAPI.startCrawling(projectId, urlList);

      // 크롤링 시작 후 상태 폴링
      pollCrawlingStatus();
    } catch (error) {
      console.error("크롤링 시작 오류:", error);
      setError("크롤링을 시작하는데 실패했습니다.");
      setCrawling(false);
    }
  };

  const pollCrawlingStatus = () => {
    const interval = setInterval(async () => {
      try {
        await fetchCrawlingStatus();

        // 크롤링이 완료되었는지 확인
        if (crawlingStatus && crawlingStatus.total_results > 0) {
          setCrawling(false);
          clearInterval(interval);
        }
      } catch (error) {
        console.error("상태 폴링 오류:", error);
      }
    }, 3000); // 3초마다 폴링

    // 5분 후 타임아웃
    setTimeout(() => {
      clearInterval(interval);
      setCrawling(false);
    }, 300000);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "completed":
        return <CheckIcon color="success" />;
      case "failed":
        return <ErrorIcon color="error" />;
      case "processing":
        return <CircularProgress size={20} />;
      default:
        return <PendingIcon color="action" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed":
        return "success";
      case "failed":
        return "error";
      case "processing":
        return "warning";
      default:
        return "default";
    }
  };

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        크롤링 및 분석
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Box
        sx={{
          display: "grid",
          gridTemplateColumns: { xs: "1fr", md: "repeat(2, 1fr)" },
          gap: 3,
        }}
      >
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              분석할 URL 입력
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              비교할 경쟁사 도움말 URL들을 한 줄에 하나씩 입력하세요.
            </Typography>

            <TextField
              fullWidth
              multiline
              rows={6}
              variant="outlined"
              label="URL 목록"
              placeholder="https://example.com/help&#10;https://competitor.com/support&#10;https://another.com/docs"
              value={urls}
              onChange={(e) => setUrls(e.target.value)}
              sx={{ mb: 2 }}
            />

            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle2" gutterBottom>
                등록된 키워드 ({keywords.length}개)
              </Typography>
              <Box sx={{ display: "flex", flexWrap: "wrap", gap: 1 }}>
                {keywords.map((keyword) => (
                  <Chip
                    key={keyword.id}
                    label={keyword.keyword}
                    size="small"
                    color="primary"
                    variant="outlined"
                  />
                ))}
              </Box>
              {keywords.length === 0 && (
                <Typography variant="body2" color="text.secondary">
                  키워드가 없습니다. 먼저 키워드를 추가해주세요.
                </Typography>
              )}
            </Box>

            <Box sx={{ display: "flex", gap: 2 }}>
              <Button
                variant="contained"
                startIcon={
                  crawling ? <CircularProgress size={20} /> : <PlayIcon />
                }
                onClick={handleStartCrawling}
                disabled={crawling || keywords.length === 0}
                fullWidth
              >
                {crawling ? "크롤링 중..." : "크롤링 시작"}
              </Button>
              <Button
                variant="outlined"
                startIcon={<RefreshIcon />}
                onClick={fetchCrawlingStatus}
                disabled={crawling}
              >
                새로고침
              </Button>
            </Box>
          </CardContent>
        </Card>

        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              크롤링 상태
            </Typography>

            {crawlingStatus && (
              <Box sx={{ mb: 3 }}>
                <Typography variant="body2" gutterBottom>
                  총 분석 결과: {crawlingStatus.total_results}개
                </Typography>
                <LinearProgress
                  variant="determinate"
                  value={crawlingStatus.total_results > 0 ? 100 : 0}
                  sx={{ mb: 1 }}
                />
              </Box>
            )}

            <Typography variant="subtitle2" gutterBottom>
              최근 크롤링 결과
            </Typography>

            {crawlingResults.length > 0 ? (
              <Box sx={{ maxHeight: 400, overflow: "auto" }}>
                {crawlingResults.map((result) => (
                  <Paper key={result.id} sx={{ p: 2, mb: 1 }}>
                    <Box
                      sx={{
                        display: "flex",
                        alignItems: "center",
                        gap: 1,
                        mb: 1,
                      }}
                    >
                      {getStatusIcon(result.status)}
                      <Typography variant="body2" sx={{ flexGrow: 1 }}>
                        {result.url}
                      </Typography>
                      <Chip
                        label={result.status}
                        size="small"
                        color={getStatusColor(result.status) as any}
                      />
                    </Box>
                    {result.error_message && (
                      <Typography variant="caption" color="error">
                        오류: {result.error_message}
                      </Typography>
                    )}
                  </Paper>
                ))}
              </Box>
            ) : (
              <Typography variant="body2" color="text.secondary">
                아직 크롤링 결과가 없습니다.
              </Typography>
            )}
          </CardContent>
        </Card>
      </Box>

      <Box sx={{ mt: 3, display: "flex", gap: 2 }}>
        <Button
          variant="outlined"
          onClick={() => navigate(`/projects/${projectId}/keywords`)}
        >
          키워드 관리
        </Button>
        <Button
          variant="outlined"
          onClick={() => navigate(`/projects/${projectId}`)}
        >
          프로젝트 상세
        </Button>
      </Box>
    </Box>
  );
};

export default CrawlingPage;
